targets.cypherQuery:
//To optimize the query is importan to filter as early as possible.
//Collect all the Interface Policy Groups and filter by name
OPTIONAL MATCH (n1:infraAccPortGrp) WHERE (n1.fabric="$fabric" and n1.name="$pg")
OPTIONAL MATCH (n2:infraAccBndlGrp) WHERE (n2.fabric="$fabric" and n2.name="$pg")
WITH coalesce(n1, n2) as PG

//Find the Interface Policy Group to AAEP relationship
MATCH (PG)-[r1:infraRsAttEntP]->(AAEP) 
//Find the Interface Policy Group to Access Port Selector relationship
MATCH (AccessPortSel:infraHPortS)-[r2:infraRsAccBaseGrp]->(PG)

//From now on all the matches are Optinal as I want to be able to display also incomplete configs
//Build PG to VLAN ID relationships
OPTIONAL MATCH (AAEP)-[r3:infraRsDomP]->(Domain)
OPTIONAL MATCH (Domain)-[r4:infraRsVlanNs]->(VlanPool)
OPTIONAL MATCH (VlanPool)-[r5:IS_PARENT]->(vlanBlock)

//Find AccessPortSel to Interface Block relationships
OPTIONAL MATCH (AccessPortSel)-[r6:IS_PARENT]->(interfaceBlk:infraPortBlk)
// Find the Switch that is used for the PG
OPTIONAL MATCH (LeafIntProf:infraAccPortP)-[r7:IS_PARENT]->(AccessPortSel)
OPTIONAL MATCH (LeafProfile:infraNodeP)-[r8:infraRsAccPortP]->(LeafIntProf)
//RETURN *
RETURN vlanBlock.from + ' - ' + vlanBlock.to as VLANIDs,
       VlanPool.name,Domain.name,AAEP.name,PG.name,LeafProfile.name,AccessPortSel.name,LeafIntProf.name,
       interfaceBlk.fromCard + '/' + interfaceBlk.fromPort + ' - ' + interfaceBlk.toCard + '/' + interfaceBlk.toPort as Interface
